import React from 'react'
import './CSS/Loginsignup.css'
import {useState} from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

function Loginsignup(props){
  const [loginForm, setloginForm] = useState({
    email: "",
    password: ""
  })


  const navigate = useNavigate()
  function logInUser(event)
  {
      axios.post({
        method: "POST",
        url: "http://127.0.0.1:5000/logintoken",
        data:
        {
          email: loginForm.email,
          password: loginForm.password,
        }
      })
      .then(function(response){
        console.log(response)
        props.setToken(response.data.access_token)
        alert("Succesfully login")
        localStorage.setItem("email", )
        navigate('/profile')
      })
      .catch((error) => {
        if(error.response){
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
          if(error.response.status === 401){
            alert("Invalid Email or Password")
        }
      }
    })
    setloginForm(({
      email: "",
      username: "",
      password: ""}))
      event.preventDefault()
}

function handleChange(event){
  const {value, name} = event.target
  setloginForm(prevNote => ({
    ...prevNote, [name]: value
  }))
}

  return (
    <div className='login'>
      <div className="login-box">
    <h2>Login</h2>
    <div className="textbox">
        <input type="text" placeholder='Email'value={loginForm.email} name='email' onChange={handleChange}/>
    </div>
    <div className="textbox">
        <input type="password" placeholder='Password' value={loginForm.password} name='password' onChange={handleChange}/>
    </div>
    <div className="remember-me">
        <input type="checkbox" id="remember" name="remember"/>
        <label>Remember Me</label>
    </div>
    <button type="button" className="btn" onClick={logInUser}>Login</button>
        </div>
      <div className="login-link">
            <Link to='/signup'>New Customer?</Link>
        </div>

    </div>
  )
}
export default Loginsignup
